import TodoList from './components/TodoList.vue'
import InputTodo from './components/InputTodo.vue'
import TodoNav from './components/TodoNav.vue'

export default {
  name: 'app',
  components: {
    TodoNav,
    InputTodo,
    TodoList,
  },
  data:function(){
    return{
      Todos : [
        {
          id:345,
          title:'The First Thing To Do Today',
          completed:false,
        },
        {
          id:678,
          title:'The Second Thing To Do Today',
          completed:false,
        },
        {
          id:912,
          title:'The Third Thing To Do Today',
          completed:false,
        },
      ],
      visibility : 'all',
    }
  },
  methods:{
    deleteAll(){
      this.Todos = [];
    },
    addNewTodo(value){
      let newId = Math.floor(Date.now());
      this.Todos.push({
        id:newId,
        title:value,
        completed:false,
      });
    },
    showView(value){
      this.visibility = value;
    },
    deleteTodo(todoId){
      let chooseDeleted = '';
      this.Todos.forEach(function(item,key){
      if(item.id === todoId){
          chooseDeleted = key;
        }});
        // console.log(chooseDeleted);
      this.Todos.splice(chooseDeleted,1);
    }
  },
  computed:{
    filterTodos(){
       if(this.visibility == 'ing'){
        let NewfilterTodo = [];
        this.Todos.forEach(function(item){
          if(item.completed == false){
            NewfilterTodo.push(item);
          }
        });
        return NewfilterTodo;
      }
      else if(this.visibility == 'done'){
        let NewfilterTodo = [];
        this.Todos.forEach(function(item){
          if(item.completed == true){
            NewfilterTodo.push(item);
          }
        });
        return NewfilterTodo;
      }
      return this.Todos;
    }
  }
}
